    private val recipes = gson.fromJson<JsonArray>(json, JsonArray::class.java).map {
        val ingredients = gson.fromJson<List<Ingredient>>(it["ingredients"])
        val servings = it["servings"].asInt
        val title = it["title"].asString
        val nutrition = it["nutrition"].asJsonObject.entrySet().associate { (name, element) ->
            val nutrition = element.asJsonObject
            val unit = if (nutrition.has("unit")) {
                nutrition["unit"].string
            } else {
                ""
            }
            name to "${nutrition["amount"].float}$unit"
        }
        Recipe(ingredients, title, nutrition, servings)
    }

    fun search(query: String): Single<Response<ServerResponse<List<Recipe>>>> {
        return Single.just(
            Response.success(
                ServerResponse(recipes.filter {
                    it.title.contains(query)
                })
            )
        )
    }
